'use strict';(function initNotifications(){let noticeEl=null;let noticeTextEl=null;let notificationTimer=null;function ensureElements(){if(!noticeEl){noticeEl=document.getElementById('notification-toast');noticeTextEl=document.getElementById('notification-toast-text')}
return Boolean(noticeEl&&noticeTextEl)}
function hide(){if(!ensureElements()){return}
noticeTextEl.textContent='';noticeEl.classList.add('hidden');if(notificationTimer){clearTimeout(notificationTimer);notificationTimer=null}}
function show(message){if(!message||!ensureElements()){return}
hide();noticeTextEl.textContent=message;noticeEl.classList.remove('hidden');notificationTimer=setTimeout(hide,3000)}
window.gameNotifications={show,hide,};document.addEventListener('DOMContentLoaded',function handleInitialNotice(){if(!ensureElements()){return}
const initial=(noticeEl.getAttribute('data-initial')||'').trim();if(initial){show(initial);noticeEl.setAttribute('data-initial','')}})})()